CREATE TABLE SCHEMA_VERSION (
	major INT NOT NULL,
	minor INT NOT NULL,
	patch INT NOT NULL,
	created TIMESTAMP DEFAULT SYSDATE NOT NULL,
	PRIMARY KEY (major, minor, patch)
);

INSERT INTO SCHEMA_VERSION(major, minor, patch) VALUES (0, 0, 0);

